import { useCallback, useEffect, useState } from "react";
import MoviesList from "./MoviesList";
import Button from "./Button";
import classes from "./styles.module.css";
import Card from "./Card";
import AddMovie from "./AddMovie";

export default function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading]=useState(false);
  const [err,setErr]=useState(null);

  /*const hanldeFetch = useCallback(async() => {

    try{

      setIsLoading(true);
      setErr(null);

      const response= await fetch("https://react-fetch-d71ef-default-rtdb.firebaseio.com/");

      if(!response.ok){
        throw new Error("Something went wrong!");
      }

      const data = await response.json();

      console.log(data);

      const transformedMovies=data.results.map((movie)=>{
        return {
                  id: movie.episode_id,
                  title: movie.title,
                  text: movie.opening_crawl ,
                  release_date: movie.release_date,
              };
      });

      setMovies(transformedMovies);

    }
    catch(error){
      setErr(error.message);
    }

    setIsLoading(false);
  },[]); */

  useEffect(()=>{
    console.log("Effect");
    hanldeFetch();
  },[hanldeFetch]);

  let content =<p>No Movies Found.</p>

  if(isLoading){
    content=<p>Loading...</p>
  }

  if(movies.length>0){
    content=<MoviesList movies={movies} />
  }

  if(err){
    content=<p>{err}</p>
  }

  const handleAddMovie= async (movie)=>{

    const response=await fetch("https://react-fetch-d71ef-default-rtdb.firebaseio.com/",{
      method:"POST",
      body:JSON.stringify(movie),
      headers:{
        "Content-Type":"application/json",
      }
    });

    const data=await response.json();

    console.log(data);

  };

  return (
    <div className={classes.App}>
      <AddMovie onAdd={handleAddMovie}/>
      <Card className={classes["btn-container"]}>
        <Button onClick={hanldeFetch}>Fetch Movies</Button>
      </Card>
      <Card className={classes.movies}>
        {content}
      </Card>
    </div>
  );
}
